    int genrateRandomNumber()
    {
        Random random = new Random();
        //Generate a random  integer between 0 (inclusive) and 100 (exclusive)
        myChoice = random.nextInt(101); 
        return (myChoice);
    }
    void setLabel()
    {
        if(counter==0)
        {
            this.setVisible(false);
            this.dispose();
            End_Game end = new End_Game();
            end.setVisible(true);
        }
        else{
            jLabel3.setText(Integer.toString(counter));
            counter -=1;
        }
    }
