    static int num;
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        if(jTextField1.getText().isEmpty())
        {
            JOptionPane.showMessageDialog(rootPane,"TextField can't be empty. Please, guess any number.");
            return;
        }
        try{
            String userChoice = jTextField1.getText();
            num = Integer.parseInt(userChoice);
            if(num < 0 || num > 100)
            JOptionPane.showMessageDialog(rootPane,"INVALID RANGE! You may allowed to chose a number between 1 to 100.");
            else{
                if(randomNumber == num)
                {
                    JOptionPane.showMessageDialog(rootPane,"Congratulations, you Win the game.");
                    this.setVisible(false);
                    this.dispose();
                    End_Game end = new End_Game();
                    end.setVisible(true);
                }
                else if(num > randomNumber)
                {
                    JOptionPane.showMessageDialog(rootPane,"Try Again, You are selected too high number.");
                    jTextField1.setText("");
                }
                else if(num < randomNumber)
                {
                    JOptionPane.showMessageDialog(rootPane,"Try Again, You are selected too low number.");
                    jTextField1.setText("");
                }
                setLabel();
            }
        }
        catch(NumberFormatException ex){
            JOptionPane.showMessageDialog(rootPane,"Please, select Integer");
            jTextField1.setText("");
        }

    }//GEN-LAST:event_jButton2ActionPerformed

